import os
from chipstack_client.client import Client


def test_generate_unit_tests():
    client = Client(os.environ["CHIPSTACK_LICENSE_KEY"])
    design_file_contents = open("./chipstack/data/dut.sv").read()
    output = client.generate_unit_tests(design_file_contents)
    assert output


if __name__ == "__main__":
    test_generate_unit_tests()
