"""
This module contains enums representing different models used in the application.
"""

from enum import Enum


class ProviderIdentifier(Enum):
    """
    Enum class representing the LLM providers.

    Attributes:
        OPENAI (str): Represents the OpenAI LLM identifier.
        AZURE_OPENAI (str): Represents the Azure OpenAI LLM identifier.
        TEXT_GENERATION_INFERENCE (str): Represents the Text Generation Inference LLM identifier.
    """

    OPENAI = "openai"
    AZURE_OPENAI = "azure_openai"
    GOOGLE = "google"
    TEXT_GENERATION_INFERENCE = "text_generation_inference"


class ModelIdentifier(Enum):
    """
    Base class for all enums in the application.
    """

    @classmethod
    def default(cls) -> "ModelIdentifier":
        raise NotImplementedError


class GoogleAIModelEnum(ModelIdentifier):
    """
    Enum representing different Google AI models.
    """

    GEMINI_1_0_PRO = "gemini-1.0-pro"
    GEMINI_1_5_PRO = "gemini-1.5-pro"
    GEMINI_1_5_PREVIEW = "gemini-1.5-pro-preview-0409"

    @classmethod
    def default(cls) -> "GoogleAIModelEnum":
        return cls.GEMINI_1_5_PREVIEW


class OpenAIModelEnum(ModelIdentifier):
    """
    Enum representing different OpenAI models.

    Attributes:
        GPT_4 (str): Model name for GPT-4.
        GPT_3_5_TURBO (str): Model name for GPT-3.5 Turbo.
        GPT_4_TURBO_PREVIEW (str): Model name for GPT-4 Turbo Preview.
    """

    GPT_4 = "gpt-4"
    GPT_3_5_TURBO = "gpt-3.5-turbo"
    GPT_4_TURBO_PREVIEW = "gpt-4-turbo-preview"

    @classmethod
    def default(cls) -> "OpenAIModelEnum":
        return cls.GPT_4_TURBO_PREVIEW


class HuggingFaceModelEnum(ModelIdentifier):
    """
    Enum class representing Hugging Face models.

    Attributes:
        MISTRALAI_MIXTRAL_8X7B_INSTRUCT_V0_1 (str): Model name for MistralAI Mixtral-8x7B Instruct v0.1.
    """

    MISTRALAI_MIXTRAL_8X7B_INSTRUCT_V0_1 = "mistralai/Mixtral-8x7B-Instruct-v0.1"

    @classmethod
    def default(cls) -> "HuggingFaceModelEnum":
        return cls.MISTRALAI_MIXTRAL_8X7B_INSTRUCT_V0_1


class AzureOpenAIModelEnum(ModelIdentifier):
    """
    Enum class representing the available Azure OpenAI models.

    Attributes:
        CHIPSTACK4 (str): Model name for Chipstack4.
        CHIPSTACK_GPT4_TURBO (str): Model name for ChipstackGpt4Turbo.
        CHIPSTACK35 (str): Model name for Chipstack35.
    """

    CHIPSTACK4 = "Chipstack4"
    CHIPSTACK_GPT4_TURBO = "ChipstackGpt4Turbo"
    CHIPSTACK35 = "Chipstack35"

    @classmethod
    def default(cls) -> "AzureOpenAIModelEnum":
        return cls.CHIPSTACK_GPT4_TURBO


class EmbeddingsProviderEnum(ModelIdentifier):
    """
    Enum class representing the available embeddings providers.

    Attributes:
        HUGGING_FACE (str): Model name for Hugging Face.
        OPEN_AI (str): Model name for OpenAI.
    """

    HUGGING_FACE = "huggingface"
    OPEN_AI = "openai"

    @classmethod
    def default(cls) -> "EmbeddingsProviderEnum":
        return cls.OPEN_AI


class EmbeddingsModelEnum(ModelIdentifier):
    """
    Enum class representing the available embeddings models.

    Attributes:
        SENTENCE_TRANSFORMERS_ALL_MINILM_L6_V2 (str): Model name for sentence-transformers/all-MiniLM-l6-v2.
    """

    SENTENCE_TRANSFORMERS_ALL_MINILM_L6_V2 = "sentence-transformers/all-MiniLM-l6-v2"
    OPEN_AI = "text-embedding-3-large"

    @classmethod
    def default(cls) -> "EmbeddingsModelEnum":
        return cls.SENTENCE_TRANSFORMERS_ALL_MINILM_L6_V2
